// if queue doesn't exist, create it
context.queue = context.queue || [];
context.busy = context.busy || false;

// if the msg is a trigger one release next message
if (msg.hasOwnProperty("trigger")) {
    if (context.queue.length > 0) {
        var m = context.queue.shift();
        return {payload:m};
    }
    else {
        context.busy = false;
    }
}
else {
    if (context.busy) {
        // if busy add to queue
        context.queue.push(msg.payload);
    }
    else {
        // otherwise we are empty so just pass through and set busy flag
        context.busy = true;
        return msg;
    }
}

return null;

// handle the return from the exec in here 
// if all is good then set msg.trigger property to exist
msg.trigger = 1
return msg;
